import React, { useState } from 'react'
import { Col, Row, Container, Form, Button } from 'react-bootstrap';
import { Link } from "react-router-dom";
import "./Signup.css";
import botImg from '../../assets/bot.jpeg'

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');

  //Image upload states
  const [image, setImage] = useState("");
  const [uploadingImg, setUploadingImg] = useState(false);
  const [imagePreview, setImagePreview] = useState(null);

  // validate img (png, jpg)
  function validateImg(e) {
    const file = e.target.files[0];
    if (file.size >= 1048578) {
      return alert('Max file size is 1mb');
    } else {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    }
  }

  async function uploadImage(){
    const data = new FormData();
    data.append('file', image);
    data.append('upload_preset', 'teeexgff');
    try {
      setUploadingImg(true);
      let res = await fetch('https://api.cloudary.com/v1_1/learn-code-10/image/upload', {
        method: 'post',
        body: data
      })
      const urlData = await res.json();
      setUploadingImg(false);
      return urlData.url
    } catch(error) {
      setUploadingImg(false);
    }
  }

  async function handleSignup (e) {
    e.preventDefault();
    if(!image) return alert('Please upload your profile picture');
    const url = await uploadImage(image);
    console.log(url);
  }

  return (
    <Container>
    <Row>
      <Col md={5} className="login__bg"></Col>
      <Col md={3} className="d-flex align-center justify-content-center flex-direction-column">
        <Form style={{ width: '80%', maxWidth: 500 }}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" />
            <Form.Text className="text-muted">We'll never share your email with anyone else.</Form.Text>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Login
          </Button>
          <div className="py-4">
            <p className="text-center">
              Don't have an account ? <Link to="/signup">Signup</Link>
            </p>
          </div>
        </Form>
      </Col>
    </Row>
  </Container>
  );
}

export default Signup;
